import React, { Component } from "react";
import CardList from "./CardList";
import SearchBox from "./SearchBox";
import { memes } from "./Meme";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      memes: memes,
      searchfield: "",
    };
  }

  onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value });
  };

  render() {
    const filterMemesByName = this.state.memes.filter((meme) => {
      return meme.name
        .toLowerCase()
        .includes(this.state.searchfield.toLowerCase());
    });
    const filterMemesByEmail = this.state.memes.filter((meme) => {
      return meme.email
        .toLowerCase()
        .includes(this.state.searchfield.toLowerCase());
    });

    return (
      <div className="tc">
        <h1 className="f1 pa5">Meme Browser</h1>
        <h2>You can search for Meme name or desciptions</h2>
        <SearchBox searchChange={this.onSearchChange} />
        <CardList memes={(filterMemesByName, filterMemesByEmail)} />
      </div>
    );
  }
}

export default App;
