import React, { Component } from "react";
import CardList from "./CardList";
import SearchBox from "./SearchBox";
import { flags } from "./Flag";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      flags: flags,
      searchfield: "",
    };
  }

  onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value });
  };

  render() {
    const filterFlagByName = this.state.flags.filter((flag) => {
      return (
        <div>
          flag.name .toLowerCase()
          .includes(this.state.searchfield.toLowerCase()); flag.email
          .toLowerCase() .includes(this.state.searchfield.toLowerCase());
        </div>
      );
    });

    return (
      <div className="tc">
        <h1 className="f1 pa5">Country Flags Browser</h1>
        <h2>Search for by Country name or by Continent </h2>
        <SearchBox searchChange={this.onSearchChange} />
        <CardList flags={filterFlagByName} />
      </div>
    );
  }
}

export default App;
