import React, { Component } from "react";
import CardList from "./CardList";
import SearchBox from "./SearchBox";
import { memes } from "./Meme";

class App extends Component {
  constructor() {
    super();
    this.state = {
      memes: memes,
      searchfield: "",
    };
  }

  onSearchChange(event) {
    console.log(event.target.value);
    const filterMemes = this.state.memes.filter((meme) => {
      return meme.name.toLowerCase().includes(searchfield.toLowerCase());
    });
  }

  render() {
    return (
      <div className="tc">
        <h1>Meme Browser</h1>
        <SearchBox searchChange={this.onSearchChange} />
        <CardList memes={this.state.memes} />
      </div>
    );
  }
}

export default App;
